import React, { useEffect, useState, useRef } from "react";
import styles from "./styles.module.css";
import { Link, useNavigate } from "react-router-dom";
import {
  Input,
  Button,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { userLogin } from "../../../../store/slices/loginSlice";
import { useDispatch, useSelector } from "react-redux";
import { RootState, AppDispatch } from "../../../../store";

const Login: React.FC = () => {
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const [isSwitchingPassword, setSwitchingPassword] = useState<boolean>(false);
  const dispatch = useDispatch<AppDispatch>();
  const navigate = useNavigate();

  // Состояние пользователя из Redux
  const { isAuthenticated, isLoading, hasError, error } = useSelector(
    (state: RootState) => state.userLogin
  );

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    dispatch(userLogin({ email, password }));
  };

  // Предотвращение бесконечного рендера
  const prevAuth = useRef(isAuthenticated);

  useEffect(() => {
    if (isAuthenticated && !prevAuth.current) {
      navigate("/profile");
    }
    prevAuth.current = isAuthenticated;
  }, [isAuthenticated, navigate]);

  return (
    <div className={styles.RegistrationBlock}>
      <h2 className={styles.title}>Вход</h2>
      <form onSubmit={handleLogin}>
        <div className={styles.InputOne}>
          <Input
            type="email"
            placeholder="E-mail"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            onPointerEnterCapture={undefined}
            onPointerLeaveCapture={undefined}
          />
        </div>
        <div className={styles.InputTwo}>
          <Input
            type={isSwitchingPassword ? "text" : "password"}
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            icon={isSwitchingPassword ? "HideIcon" : "ShowIcon"}
            onIconClick={() => setSwitchingPassword(!isSwitchingPassword)}
            onPointerEnterCapture={undefined}
            onPointerLeaveCapture={undefined}
          />
        </div>
        <div className={styles.buttom}>
          <Button
            htmlType="submit"
            type="primary"
            size="medium"
            disabled={isLoading}
          >
            {isLoading ? "Загрузка..." : "Войти"}
          </Button>
        </div>
      </form>
      {hasError && (
        <p className={styles.errorText}>Ошибка: {error || "Ошибка входа"}</p>
      )}
      <div className={styles.footerLinks}>
        <p className="text text_type_main-default">
          Вы новый пользователь?
          <Link to="/register" className={styles.link}>
            Зарегистрироваться
          </Link>
        </p>
        <p className="text text_type_main-default mt-2">
          Забыли пароль?
          <Link to="/forgot-password" className={styles.link}>
            Восстановить пароль
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
