import React, { useEffect, useState } from "react";

interface Ingredient {
  _id: string;
  name: string;
  image: string;
  calories: number;
  proteins: number;
  fat: number;
  carbohydrates: number;
}

const FetchData = () => {
  const [ingredients, setIngredients] = useState<Ingredient[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetch("https://norma.nomoreparties.space/api/ingredients") //Отправляем запрос
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Ошибка`);
        }
        return response.json();
      })
      .then((data) => {
        if (data.success) {
          setIngredients(data.data);
        } else {
          throw new Error(`Ошибка при получении данных`);
        }
      })
      .catch((error) => setError(error.message))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>загрузка ингредиентов...</p>;
  if (error) return <p> Ошибка {error}</p>;
  return (
    <>
      <ul>
        {ingredients.map((ingredients) => (
          <li key={ingredients._id}>
            <img
              src={ingredients.image}
              alt={ingredients.name}
              width="50"
              height="50"
              style={{ marginRight: "10px" }}
            />
            <strong>{ingredients.name}</strong> - {ingredients.calories} ккал
          </li>
        ))}
      </ul>
    </>
  );
};
export default FetchData;
