import React, { useRef, useState, useEffect } from "react";
import ingridient from "./ingridient.module.css";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { Product } from "../../../types/productTypes";
import { IngredientsList } from "./IngredientsList";
import { IngredientModal } from "./IngredientModal";

// Функция сортировки ингредиентов по типу
const sortProducts = (products: Product[]): Product[] => {
  const order = ["bun", "sauce", "main"];
  return products
    .slice()
    .sort((a, b) => order.indexOf(a.type) - order.indexOf(b.type));
};

const BurgerIngridients: React.FC = () => {
  // Локальное состояние для данных, загрузки и ошибок
  const [state, setState] = useState({
    isLoading: false,
    hasError: false,
    data: [] as Product[],
  });

  // Функция для получения ингредиентов с API
  const getIngredients = () => {
    setState((prev) => ({ ...prev, hasError: false, isLoading: true }));
    fetch("https://norma.nomoreparties.space/api/ingredients")
      .then((res) => res.json())
      .then((data) =>
        // Ожидается, что API возвращает объект вида { data: Product[] }
        setState((prev) => ({
          ...prev,
          data: data.data as Product[],
          isLoading: false,
        }))
      )
      .catch((e) => {
        setState((prev) => ({ ...prev, hasError: true, isLoading: false }));
      });
  };

  useEffect(() => {
    getIngredients();
  }, []);

  // Сортировка ингредиентов, полученных с API
  const sortedProducts = sortProducts(state.data);

  // Текущая выбранная вкладка
  const [current, setCurrent] = useState("bun");

  // Рефы для прокрутки к каждой категории
  const bunRef = useRef<HTMLDivElement>(null);
  const sauceRef = useRef<HTMLDivElement>(null);
  const mainRef = useRef<HTMLDivElement>(null);

  // Состояние для модального окна
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [isModalActive, setIsModalActive] = useState(false);

  // Обработчик для переключения вкладок и прокрутки к нужной секции
  const handleTabClick = (value: string) => {
    setCurrent(value);
    switch (value) {
      case "bun":
        bunRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "sauce":
        sauceRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "main":
        mainRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      default:
        break;
    }
  };

  // Открытие модального окна по клику на ингредиент
  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    setIsModalActive(true);
  };

  // Закрытие модального окна
  const handleModalClose = () => {
    setIsModalActive(false);
  };

  // Закрытие модального окна по нажатию клавиши Esc
  const handleModalKeyClose = (event: KeyboardEvent) => {
    if (event.key === "Escape") {
      handleModalClose();
    }
  };

  useEffect(() => {
    document.addEventListener("keydown", handleModalKeyClose);
    return () => {
      document.removeEventListener("keydown", handleModalKeyClose);
    };
  }, []);

  return (
    <>
      <h1
        className={`mt-9 mb-6 text text_type_main-large ${ingridient.ingridientTitle}`}
      >
        Список продуктов
      </h1>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={current === "bun"}
          onClick={() => handleTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={current === "sauce"}
          onClick={() => handleTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={current === "main"}
          onClick={() => handleTabClick("main")}
        >
          Начинки
        </Tab>
      </div>
      {state.isLoading && <p>Загрузка...</p>}
      {state.hasError && <p>Произошла ошибка при загрузке ингредиентов</p>}

      {/* Передаём отсортированные ингредиенты в компонент IngredientsList */}
      <IngredientsList
        sortedProducts={sortedProducts}
        onProductClick={handleProductClick}
        bunRef={bunRef}
        sauceRef={sauceRef}
        mainRef={mainRef}
      />

      {selectedProduct && (
        <IngredientModal
          product={selectedProduct}
          isActive={isModalActive}
          onClose={handleModalClose}
        />
      )}
    </>
  );
};

export default BurgerIngridients;
