import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { Product } from "../../types/productTypes";

interface OrderState {
  order: Product[];
}

const initialState: OrderState = {
  order: [],
};
const orderSlice = createSlice({
  name: "order",
  initialState,
  reducers: {
    addProduct: (state, action: PayloadAction<Product>) => {
      state.order.push(action.payload); //Добавляем ингредиент
    },
    removeProduct: (state, action: PayloadAction<number>) => {
      state.order = state.order.filter((_, index) => index !== action.payload); //Удаляем ингредиент по индексу
    },

    clearOrder: (state) => {
      state.order = [];
    }, // очищаем заказ
  },
});

export const { addProduct, removeProduct, clearOrder } = orderSlice.actions;
export default orderSlice.reducer;
