import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { Product } from "../../types/productTypes";

interface IngredientsState {
  ingredients: Product[];
  isLoading: boolean;
  hasError: boolean;
}

// Начальное состояние
const initialState: IngredientsState = {
  ingredients: [],
  isLoading: false,
  hasError: false,
};

export const fetchIngredients = createAsyncThunk<Product[], void>(
  "ingredients/fetchIngredients",
  async (_, { rejectWithValue }) => {
    try {
      const response = await fetch(
        "https://norma.nomoreparties.space/api/ingredients"
      );
      const data = await response.json();
      return data.data as Product[];
    } catch (error) {
      return rejectWithValue("Ошибка загрузки ингредиентов");
    }
  }
);

const ingewdientsSlice = createSlice({
  name: "ingredients",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchIngredients.pending, (state) => {
        state.isLoading = true;
        state.hasError = false;
      })
      .addCase(fetchIngredients.pending, (state, action) => {
        state.isLoading = false;
        state.ingredients = action.payload ?? []; // Этот оператор ?? (nullish coalescing) гарантирует, что если action.payload будет undefined, то присвоится пустой массив [].
      })
      .addCase(fetchIngredients.pending, (state) => {
        state.isLoading = false;
        state.hasError = true;
      });
  },
});
export default ingewdientsSlice.reducer;
