import React, { useRef, useState, useEffect } from "react";
import { Product } from "../../../types/productTypes";
import ingridient from "./ingridient.module.css";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";

interface IngredientListProps {
  sortedProducts: Product[];
  onProductClick: (product: Product) => void;
}

export const IngredientsList: React.FC<IngredientListProps> = ({
  sortedProducts,
  onProductClick,
}) => {
  const bunRef = useRef<HTMLDivElement>(null);
  const sauceRef = useRef<HTMLDivElement>(null);
  const mainRef = useRef<HTMLDivElement>(null);
  return (
    <section className={ingridient.ingridients}>
      <div className="mt-9 mb-9" ref={bunRef}>
        <h2
          className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
        >
          Булки
        </h2>
        <ul className={ingridient.ingridientsContainer}>
          {sortedProducts
            .filter((product) => product.type === "bun")
            .map((product) => (
              <li
                className={ingridient.ingridientsItem}
                key={product._id}
                onClick={() => onProductClick(product)}
              >
                <img
                  className="ml-2 mr-2"
                  src={product.image}
                  alt={product.name}
                />
                <div className={ingridient.priceBox}>
                  <p className={`text text_type_main-default`}>
                    {product.price}
                  </p>
                  <CurrencyIcon type="primary" />
                </div>
                <h3 className={`text text_type_main-default`}>
                  {product.name}
                </h3>
              </li>
            ))}
        </ul>
      </div>
      <div ref={sauceRef}>
        <h2
          className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
        >
          Соусы
        </h2>
        <ul className={ingridient.ingridientsContainer}>
          {sortedProducts
            .filter((product) => product.type === "sauce")
            .map((product) => (
              <li
                className={ingridient.ingridientsItem}
                key={product._id}
                onClick={() => onProductClick(product)}
              >
                <img
                  className="ml-2 mr-2"
                  src={product.image}
                  alt={product.name}
                />
                <div className={ingridient.priceBox}>
                  <p className={`text text_type_main-default`}>
                    {product.price}
                  </p>
                  <CurrencyIcon type="primary" />
                </div>
                <h3 className={`text text_type_main-default`}>
                  {product.name}
                </h3>
              </li>
            ))}
        </ul>
      </div>
      <div ref={mainRef} className="">
        <h2
          className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
        >
          Начинки
        </h2>
        <ul className={ingridient.ingridientsContainer}>
          {sortedProducts
            .filter((product) => product.type === "main")
            .map((product) => (
              <li
                className={ingridient.ingridientsItem}
                key={product._id}
                onClick={() => onProductClick(product)}
              >
                <img
                  className="ml-2 mr-2"
                  src={product.image}
                  alt={product.name}
                />
                <div className={ingridient.priceBox}>
                  <p className={`text text_type_main-default`}>
                    {product.price}
                  </p>
                  <CurrencyIcon type="primary" />
                </div>
                <h3 className={`text text_type_main-default`}>
                  {product.name}
                </h3>
              </li>
            ))}
        </ul>
      </div>
    </section>
  );
};
