import React, { useRef, useState, useEffect } from "react";
import ingridient from "./ingridient.module.css";
import { Product } from "../../../types/productTypes";
import { IngredientsList } from "./IngredientsList";
import { IngredientModal } from "./IngredientModal";
import TabNavigation from "./TabNavigation";
import { useSelector, useDispatch } from "react-redux";
import { RootState, AppDispatch } from "../../../store/";
import { fetchIngredients } from "../../../store/slices/ingredientsSlice";
import { addProduct } from "../../../store/slices/orderSlice";

const sortProducts = (products: Product[]): Product[] => {
  const order = ["bun", "sauce", "main"];
  return products
    .slice()
    .sort((a, b) => order.indexOf(a.type) - order.indexOf(b.type));
};

// üÜï –¢–µ–ø–µ—Ä—å –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –ø—Ä–∏–Ω–∏–º–∞–µ—Ç `onAddProduct`
interface BurgerIngredientsProps {
  onAddProduct: (product: Product) => void;
}

const BurgerIngredients: React.FC<BurgerIngredientsProps> = ({
  onAddProduct,
}) => {
  const [current, setCurrent] = useState("bun");
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [isModalActive, setIsModalActive] = useState(false);

  const [state, setState] = useState({
    isLoading: false,
    hasError: false,
    data: [] as Product[],
  });

  const dispatch = useDispatch<AppDispatch>();
  const { ingredients, isLoading, hasError } = useSelector(
    (state: RootState) => state.ingredients
  );
  useEffect(() => {
    dispatch(fetchIngredients());
  }, [dispatch]);
  // useEffect(() => {
  //   setState((prev) => ({ ...prev, isLoading: true }));
  //   fetch("https://norma.nomoreparties.space/api/ingredients")
  //     .then((res) => res.json())
  //     .then((data) =>
  //       setState((prev) => ({ ...prev, data: data.data, isLoading: false }))
  //     )
  //     .catch(() =>
  //       setState((prev) => ({ ...prev, hasError: true, isLoading: false }))
  //     );
  // }, []);

  // const sortedProducts = sortProducts(state.data);

  const bunRef = useRef<HTMLDivElement>(null);
  const sauceRef = useRef<HTMLDivElement>(null);
  const mainRef = useRef<HTMLDivElement>(null);

  const handleTabClick = (value: string) => {
    setCurrent(value);
    switch (value) {
      case "bun":
        bunRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "sauce":
        sauceRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "main":
        mainRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      default:
        break;
    }
  };

  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    setIsModalActive(true);
  };

  // ‚úÖ –î–æ–±–∞–≤–ª—è–µ–º —Ç–æ–≤–∞—Ä –≤ Redux
  const handleAddProduct = (product: Product) => {
    dispatch(addProduct(product));
  };
  const handleModalClose = () => {
    setIsModalActive(false);
  };

  return (
    <>
      <h1
        className={`mt-9 mb-6 text text_type_main-large ${ingridient.ingridientTitle}`}
      >
        –°–ø–∏—Å–æ–∫ –ø—Ä–æ–¥—É–∫—Ç–æ–≤
      </h1>
      <TabNavigation current={current} onTabClick={handleTabClick} />

      {isLoading && <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>}
      {hasError && <p>–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏–Ω–≥—Ä–µ–¥–∏–µ–Ω—Ç–æ–≤</p>}

      <IngredientsList
        sortedProducts={ingredients}
        onProductClick={handleProductClick}
        onAddProduct={handleAddProduct} // –ü–µ—Ä–µ–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –∑–∞–∫–∞–∑–∞
        bunRef={bunRef}
        sauceRef={sauceRef}
        mainRef={mainRef}
      />

      {selectedProduct && (
        <IngredientModal
          product={selectedProduct}
          isActive={isModalActive}
          onClose={handleModalClose}
        />
      )}
    </>
  );
};

export default BurgerIngredients;
