import React from "react";
import { Product } from "../../../types/productTypes";
import ingridient from "./ingridient.module.css";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { useDraggable } from "@dnd-kit/core";

interface IngredientListProps {
  sortedProducts: Product[];
  onProductClick: (product: Product) => void;
  bunRef: React.RefObject<HTMLDivElement>;
  sauceRef: React.RefObject<HTMLDivElement>;
  mainRef: React.RefObject<HTMLDivElement>;
}

// Новый компонент для перетаскиваемого ингредиента
interface DraggableIngredientProps {
  product: Product;
  onProductClick: (product: Product) => void;
}

const DraggableIngredient: React.FC<DraggableIngredientProps> = ({
  product,
  onProductClick,
}) => {
  // Используем useDraggable. Передаём уникальный id и сами данные ингредиента.
  const { attributes, listeners, setNodeRef, transform } = useDraggable({
    id: product._id,
    data: product,
  });

  // Применяем transform, если элемент перемещается
  const style = {
    transform: transform
      ? `translate3d(${transform.x}px, ${transform.y}px, 0)`
      : undefined,
  };

  return (
    <li
      ref={setNodeRef}
      style={style}
      {...listeners}
      {...attributes}
      onClick={() => onProductClick(product)}
      className={ingridient.ingridientsItem}
    >
      <img className="ml-2 mr-2" src={product.image} alt={product.name} />
      <div className={ingridient.priceBox}>
        <p className="text text_type_main-default">{product.price}</p>
        <CurrencyIcon type="primary" />
      </div>
      <h3 className="text text_type_main-default">{product.name}</h3>
      {/* Дополнительная кнопка, если требуется */}
      <button> Lj</button>
    </li>
  );
};

export const IngredientsList: React.FC<IngredientListProps> = ({
  sortedProducts,
  onProductClick,
  bunRef,
  sauceRef,
  mainRef,
}) => {
  return (
    <section className={ingridient.ingridients}>
      <div className="mt-9 mb-9" ref={bunRef}>
        <h2
          className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
        >
          Булки
        </h2>
        <ul className={ingridient.ingridientsContainer}>
          {sortedProducts
            .filter((product) => product.type === "bun")
            .map((product) => (
              <DraggableIngredient
                key={product._id}
                product={product}
                onProductClick={onProductClick}
              />
            ))}
        </ul>
      </div>
      <div ref={sauceRef}>
        <h2
          className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
        >
          Соусы
        </h2>
        <ul className={ingridient.ingridientsContainer}>
          {sortedProducts
            .filter((product) => product.type === "sauce")
            .map((product) => (
              <li
                className={ingridient.ingridientsItem}
                key={product._id}
                onClick={() => onProductClick(product)}
              >
                <img
                  className="ml-2 mr-2"
                  src={product.image}
                  alt={product.name}
                />
                <div className={ingridient.priceBox}>
                  <p className="text text_type_main-default">{product.price}</p>
                  <CurrencyIcon type="primary" />
                </div>
                <h3 className="text text_type_main-default">{product.name}</h3>
              </li>
            ))}
        </ul>
      </div>
      <div ref={mainRef}>
        <h2
          className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
        >
          Начинки
        </h2>
        <ul className={ingridient.ingridientsContainer}>
          {sortedProducts
            .filter((product) => product.type === "main")
            .map((product) => (
              <li
                className={ingridient.ingridientsItem}
                key={product._id}
                onClick={() => onProductClick(product)}
              >
                <img
                  className="ml-2 mr-2"
                  src={product.image}
                  alt={product.name}
                />
                <div className={ingridient.priceBox}>
                  <p className="text text_type_main-default">{product.price}</p>
                  <CurrencyIcon type="primary" />
                </div>
                <h3 className="text text_type_main-default">{product.name}</h3>
              </li>
            ))}
        </ul>
      </div>
    </section>
  );
};
